/**
 * Calcula una fecha futura sumando un intervalo de tiempo a la fecha y hora actuales.
 * @function obtenerFechaVencimiento
 * @param {object} [extra={}] - Objeto que contiene el intervalo de tiempo a agregar.
 * @param {number} [extra.dias=0] - Cantidad de d√≠as a agregar.
 * @param {number} [extra.horas=0] - Cantidad de horas a agregar.
 * @param {number} [extra.minutos=0] - Cantidad de minutos a agregar.
 * @param {number} [extra.segundos=0] - Cantidad de segundos a agregar.
 * @returns {Date} Un objeto Date que representa la fecha y hora de vencimiento calculada.
 */
const obtenerFechaVencimiento = function({ dias = 0, horas = 0, minutos = 0, segundos = 0 } = {}) {
  const ahora = new Date();
  const milisegundosParaAgregar = (dias * 86400000) + (horas * 3600000) + (minutos * 60000) + (segundos * 1000);
  
  ahora.setTime(ahora.getTime() + milisegundosParaAgregar);
  return ahora;
};

module.exports = { obtenerFechaVencimiento };