/**
 * @module utils
 * @description Módulo central que consolida y exporta un conjunto de funciones de utilidad
 * para la aplicación. Incluye herramientas para la gestión de contraseñas,
 * la generación y verificación de tokens JWT, y cálculos de fechas.
 */

// Importa funciones para la gestión de tokens (JWT).
const { generarToken, verificarToken } = require('./funciones/token');

// Importa funciones para el hasheo y verificación de contraseñas.
const { hashPass, verificarPass } = require('./funciones/pass');

// Importa funciones de propósito general, como el cálculo de fechas.
const { obtenerFechaVencimiento } = require('./funciones/general');

// Exporta todas las funciones importadas para que estén disponibles en otras partes de la aplicación.
module.exports = {
  hashPass,
  verificarPass,
  generarToken,
  verificarToken,
  obtenerFechaVencimiento
};